Attraction.create!([
  {name: "Tower of London", destination_id: 2, description: "Founded by William the Conqueror in 1066, and enlarged and modified by successive sovereigns, the Tower is today one of the world's most famous and spectacular fortresses.", hours: "Tue - Sat: 9:00 AM - 4:30 PM; Sun - Mon 10:00 AM - 4:30 PM. Open till 5:30 PM March 1 - October 31.", cost: "£9.50 (Children, purchased online) to £22.50 (Adult, purchased at the gate). Family, student, senior discounts available."},
  {name: "City Hall Station", destination_id: 3, description: "The first NYC subway was built and operated by the Interborough Rapid Transit Company (IRT) and opened on October 27, 1904, to the joy of New York elevated train riders. In 1940, the IRT became the IRT Division after it was integrated with the city-owned New York Subway system. ", hours: "Check with Museum", cost: "Free with Museum Admission"},
  {name: "Taj Mahal", destination_id: 1, description: "The Taj Mahal is an ivory-white marble mausoleum on the south bank of the Yamuna river in the Indian city of Agra. It was commissioned in 1632 by the Mughal emperor, Shah Jahan, to house the tomb of his favourite wife, Mumtaz Mahal", hours: "N/A", cost: "Cost: Rs. 1000 ($14.92)"},
  {name: "Red Fort", destination_id: 1, description: "The Red Fort was the residence of the Mughal emperor for nearly 200 years, until 1857. It is located in the centre ofDelhi and houses a number of museums.", hours: "Hours: Sunrise - Sunset", cost: "Rs. 550 (USD $8.20)"},
  {name: "Gateway of India", destination_id: 4, description: "The Gateway of India was built to commemorate the visit of King George V and Queen Mary to Mumbai, prior to the Delhi Durbar, in December 1911. It is known as the official welcoming site into the country.", hours: "Hours: 24/7 – Best around nighttime with less visitors!", cost: "Free"},
  {name: "Jaisalmer Fort", destination_id: 6, description: "Built in 1156 AD, the Jaisalmer Fort is one of the largest fortifications on the world. The fort stands amidst the sandy expanse of the great Thar Desert, on Trikuta Hill, and has been the scene of many battles.", hours: "9am - 5pm", cost: "Free"},
  {name: "Adalaj Stepwell", destination_id: 5, description: "Mahmud Begada built the Adalaj Stepwelll in 1499 for his queen Rudabai, wife of Veersinh, the Vaghela chieftain. Such step wells were once integral to the semi-arid regions of Gujarat, as they provided water for drinking, washing and bathing. These wells were also venues for colourful festivals and sacred rituals.", hours: "24/7", cost: "Free"},
  {name: "Roosevelt Island Smallpox Hospital Ruins", destination_id: 3, description: "Crumbling hospital from 1850s on Roosevelt Island. ", hours: "N/A", cost: "Suggested $1 Donation"},
  {name: "Please Don't Tell", destination_id: 3, description: "If you can get over your self-consciousness, and the line isn't unbearable, PDT is a pretty cool experience. Enter the phone booth at the side of the restaurant, and wait, assuming you get through, because the coolest part of the experience is just seconds away.", hours: "6PM - 2AM", cost: "N/A"},
  {name: "Lower East Side Toy Company(The Back Room)", destination_id: 3, description: "Through the gate that reads \"Lower East Side Toy Company\" make your way along an historic path to the Back Room, a true-to-life 1920s era speakeasy.", hours: "7:30PM - 3:00AM", cost: "N/A"},
  {name: "Gold Bar", destination_id: nil, description: "With tables custom-made to look like Cartier bracelets, everything is designed to be a constant reminder of the glamorous elitism that patrons get to be a part of.", hours: "11PM - 4AM", cost: "$$$$"},
  {name: "Gold Bar", destination_id: 3, description: "With tables custom-made to look like Cartier bracelets, everything is designed to be a constant reminder of the glamorous elitism that patrons get to be a part of.", hours: "11PM - 4AM", cost: "$$$$"},
  {name: "British Museum", destination_id: 2, description: "A vast repository of the world's cultures, with entire sections devoted to Egyptian, Greek, and Middle Eastern artifacts, as well as the piece which united them all, the Rosetta Stone.", hours: "Sat-Wed 10:00 AM - 5:30 PM, Thu-Fri 10:00 AM - 8:30 PM", cost: "Free (suggested donation £3)"},
  {name: "Globe Theater", destination_id: 2, description: "Sam Wanamaker's largely authentic reconstruction of Shakespeare's Globe Theatre, built using traditional techniques, stages plays in the Summer months.", hours: "Vary by performance", cost: "£5 - £45 per ticket"}
])
